// Confetti particle component
const ConfettiParticle = ({
  delay,
  color,
  size,
}: {
  delay: number;
  color: string;
  size: number;
}) => {
  return (
    <motion.div
      className="absolute rounded-full"
      style={{
        backgroundColor: color,
        width: size,
        height: size,
        left: `${Math.random() * 100}%`,
        top: `${Math.random() * 20}%`,
      }}
      initial={{
        opacity: 0,
        y: -20,
        rotate: 0,
        scale: 0,
      }}
      animate={{
        opacity: [0, 1, 1, 0],
        y: [0, 100, 200, 300],
        rotate: [0, 180, 360],
        scale: [0, 1, 1, 0],
      }}
      transition={{
        duration: 3,
        delay: delay,
        ease: "easeOut",
      }}
    />
  );
};

// Email provider component
const EmailProvider = ({
  name,
  icon,
  url,
  delay,
}: {
  name: string;
  icon: React.ReactNode;
  url: string;
  delay: number;
}) => {
  return (
    <motion.a
      href={url}
      target="_blank"
      rel="noopener noreferrer"
      className="flex flex-col items-center justify-center p-4 bg-white border border-gray-200 rounded-xl hover:border-primary hover:shadow-md transition-all duration-200 group"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay, duration: 0.4 }}
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
    >
      <div className="mb-2 group-hover:scale-110 transition-transform duration-200">
        {icon}
      </div>
      <span className="text-xs font-medium text-gray-700 group-hover:text-primary transition-colors duration-200">
        {name}
      </span>
    </motion.a>
  );
};

